// Generate detailed HTML from departmentBreakdown
const data = $input.first().json.body;
const breakdown = data.departmentBreakdown || [];
const pricing = data.pricing || {};
const lang = data.metadata?.lang || 'fr';
const duration = data.duration || 4;

// Helper function to format price
const formatPrice = (price) => Math.round(price).toLocaleString('fr-FR');

// Department colors based on ID
const deptColors = {
  'seo': { bg: '#EEF2FF', border: '#818CF8', text: '#4F46E5', icon: '#6366F1' },
  'google-ads': { bg: '#EFF6FF', border: '#60A5FA', text: '#2563EB', icon: '#3B82F6' },
  'paid-social': { bg: '#F5F3FF', border: '#A78BFA', text: '#7C3AED', icon: '#8B5CF6' },
  'emailing': { bg: '#FFFBEB', border: '#FBBF24', text: '#D97706', icon: '#F59E0B' },
  'ai-training': { bg: '#ECFDF5', border: '#34D399', text: '#059669', icon: '#10B981' },
  'ai-solutions': { bg: '#F5F3FF', border: '#A78BFA', text: '#7C3AED', icon: '#8B5CF6' },
  'tracking': { bg: '#ECFEFF', border: '#22D3EE', text: '#0891B2', icon: '#06B6D4' }
};

const defaultColors = { bg: '#F9FAFB', border: '#D1D5DB', text: '#374151', icon: '#6B7280' };

// Generate department HTML blocks
let departmentsHtml = '';
let totalMonthly = 0;
let totalOneOff = 0;
let totalMediaBudget = 0;

breakdown.forEach(dept => {
  const colors = deptColors[dept.id] || defaultColors;

  if (dept.isNotSure) {
    // "Not sure" department - show as amber box
    departmentsHtml += `
      <div style="background: #FEF3C7; border: 2px solid #F59E0B; border-radius: 12px; padding: 16px; margin-bottom: 16px;">
        <div style="display: flex; align-items: center; gap: 12px;">
          <span style="font-size: 24px;">${dept.icon}</span>
          <div style="flex: 1;">
            <h3 style="margin: 0; color: #92400E; font-size: 16px; font-weight: 600;">${dept.name}</h3>
            <p style="margin: 4px 0 0 0; color: #B45309; font-size: 14px;">ðŸ’¬ ${lang === 'fr' ? 'Ã€ discuter ensemble' : 'To discuss together'}</p>
          </div>
        </div>
      </div>
    `;
    return;
  }

  // Normal department with services
  let servicesHtml = '';
  dept.services.forEach(service => {
    const priceText = service.price > 0 
      ? `${formatPrice(service.price)}â‚¬${service.isOneOff ? '' : '/mois'}` 
      : (service.level || '');
    servicesHtml += `
      <tr>
        <td style="padding: 8px 12px; border-bottom: 1px solid #E5E7EB; color: #374151; font-size: 14px;">
          ${service.name}${service.level && service.price > 0 ? ` <span style="color: #6B7280; font-size: 12px;">(${service.level})</span>` : ''}
        </td>
        <td style="padding: 8px 12px; border-bottom: 1px solid #E5E7EB; text-align: right; font-weight: 500; color: ${colors.text}; font-size: 14px;">
          ${priceText}
        </td>
      </tr>
    `;
  });

  // Management fee row
  if (dept.managementFee > 0) {
    servicesHtml += `
      <tr>
        <td style="padding: 8px 12px; border-bottom: 1px solid #E5E7EB; color: #374151; font-size: 14px;">
          ${lang === 'fr' ? 'Frais de gestion publicitaire' : 'Ad management fee'}
        </td>
        <td style="padding: 8px 12px; border-bottom: 1px solid #E5E7EB; text-align: right; font-weight: 500; color: ${colors.text}; font-size: 14px;">
          ${formatPrice(dept.managementFee)}â‚¬/mois
        </td>
      </tr>
    `;
  }

  // Media budget row
  if (dept.mediaBudget > 0) {
    servicesHtml += `
      <tr>
        <td style="padding: 8px 12px; border-bottom: 1px solid #E5E7EB; color: #F59E0B; font-size: 14px;">
          ${lang === 'fr' ? 'Budget mÃ©dia (non inclus)' : 'Media budget (not included)'}
        </td>
        <td style="padding: 8px 12px; border-bottom: 1px solid #E5E7EB; text-align: right; font-weight: 500; color: #F59E0B; font-size: 14px;">
          ${formatPrice(dept.mediaBudget)}â‚¬/mois
        </td>
      </tr>
    `;
    totalMediaBudget += dept.mediaBudget;
  }

  // Calculate subtotals
  totalMonthly += dept.monthlySubtotal;
  totalOneOff += dept.oneOffSubtotal;

  // Subtotal row
  let subtotalHtml = '';
  if (dept.monthlySubtotal > 0 || dept.oneOffSubtotal > 0) {
    const parts = [];
    if (dept.monthlySubtotal > 0) parts.push(`${formatPrice(dept.monthlySubtotal)}â‚¬/mois`);
    if (dept.oneOffSubtotal > 0) parts.push(`${formatPrice(dept.oneOffSubtotal)}â‚¬ one-off`);

    subtotalHtml = `
      <tr style="background: ${colors.bg};">
        <td style="padding: 10px 12px; font-weight: 600; color: ${colors.text}; font-size: 14px;">
          ${lang === 'fr' ? 'Sous-total' : 'Subtotal'} ${dept.name}
        </td>
        <td style="padding: 10px 12px; text-align: right; font-weight: 700; color: ${colors.text}; font-size: 15px;">
          ${parts.join(' + ')}
        </td>
      </tr>
    `;
  }

  departmentsHtml += `
    <div style="background: white; border: 2px solid ${colors.border}; border-radius: 12px; overflow: hidden; margin-bottom: 16px;">
      <div style="background: ${colors.bg}; padding: 14px 16px; border-bottom: 1px solid ${colors.border};">
        <div style="display: flex; align-items: center; gap: 12px;">
          <span style="font-size: 28px;">${dept.icon}</span>
          <h3 style="margin: 0; color: ${colors.text}; font-size: 18px; font-weight: 700;">${dept.name}</h3>
        </div>
      </div>
      <table style="width: 100%; border-collapse: collapse;">
        ${servicesHtml}
        ${subtotalHtml}
      </table>
    </div>
  `;
});

// Generate totals section
const discountPct = pricing.discountPercentage || 0;
const monthlyAfterDiscount = pricing.monthlyAfterDiscount || 0;
const grandTotal = pricing.grandTotalWithoutBudget || 0;

let totalsHtml = `
  <div style="background: linear-gradient(135deg, #1e3a5f 0%, #3b82f6 100%); border-radius: 12px; padding: 24px; color: white; margin-top: 24px;">
    <h3 style="margin: 0 0 16px 0; font-size: 18px; font-weight: 600; border-bottom: 1px solid rgba(255,255,255,0.2); padding-bottom: 12px;">ðŸ’° ${lang === 'fr' ? 'RÃ©capitulatif financier' : 'Financial Summary'}</h3>
    <table style="width: 100%; border-collapse: collapse;">
`;

if (totalMonthly > 0) {
  totalsHtml += `
    <tr>
      <td style="padding: 8px 0; color: rgba(255,255,255,0.85); font-size: 14px;">${lang === 'fr' ? 'Services mensuels' : 'Monthly services'}</td>
      <td style="padding: 8px 0; text-align: right; font-weight: 500; font-size: 14px;">${formatPrice(totalMonthly)}â‚¬/mois</td>
    </tr>
  `;
}

if (discountPct > 0) {
  totalsHtml += `
    <tr>
      <td style="padding: 8px 0; color: #86EFAC; font-size: 14px;">${lang === 'fr' ? 'Remise engagement' : 'Commitment discount'} ${duration} mois</td>
      <td style="padding: 8px 0; text-align: right; color: #86EFAC; font-weight: 500; font-size: 14px;">-${discountPct}%</td>
    </tr>
  `;
}

if (monthlyAfterDiscount > 0) {
  totalsHtml += `
    <tr>
      <td style="padding: 8px 0; color: rgba(255,255,255,0.85); font-size: 14px; font-weight: 600;">${lang === 'fr' ? 'Mensuel aprÃ¨s remise' : 'Monthly after discount'}</td>
      <td style="padding: 8px 0; text-align: right; font-weight: 700; font-size: 16px;">${formatPrice(monthlyAfterDiscount)}â‚¬/mois</td>
    </tr>
  `;
}

if (totalOneOff > 0) {
  totalsHtml += `
    <tr>
      <td style="padding: 8px 0; color: rgba(255,255,255,0.85); font-size: 14px;">${lang === 'fr' ? 'Services one-off' : 'One-time services'}</td>
      <td style="padding: 8px 0; text-align: right; font-weight: 500; font-size: 14px;">${formatPrice(totalOneOff)}â‚¬</td>
    </tr>
  `;
}

if (totalMediaBudget > 0) {
  totalsHtml += `
    <tr>
      <td style="padding: 8px 0; color: #FCD34D; font-size: 14px;">${lang === 'fr' ? 'Budget mÃ©dia (non inclus)' : 'Media budget (not included)'}</td>
      <td style="padding: 8px 0; text-align: right; color: #FCD34D; font-weight: 500; font-size: 14px;">${formatPrice(totalMediaBudget)}â‚¬/mois</td>
    </tr>
  `;
}

totalsHtml += `
    <tr style="border-top: 2px solid rgba(255,255,255,0.3);">
      <td style="padding: 16px 0 8px 0; font-size: 18px; font-weight: 700;">${lang === 'fr' ? 'TOTAL' : 'TOTAL'} (${duration} mois)</td>
      <td style="padding: 16px 0 8px 0; text-align: right; font-size: 28px; font-weight: 800;">${grandTotal > 0 ? formatPrice(grandTotal) + 'â‚¬' : (lang === 'fr' ? 'Ã€ discuter' : 'To discuss')}</td>
    </tr>
  </table>
</div>
`;

return {
  json: {
    ...data,
    departmentsHtml,
    totalsHtml,
    timestamp: new Date().toLocaleString('fr-FR', { day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit' }),
    langFlag: lang === 'en' ? 'ðŸ‡¬ðŸ‡§ EN' : 'ðŸ‡«ðŸ‡· FR',
    monthlyAfterDiscount: formatPrice(monthlyAfterDiscount),
    grandTotal: grandTotal > 0 ? formatPrice(grandTotal) : (lang === 'fr' ? 'Ã€ discuter' : 'To discuss'),
    hasNotSure: breakdown.some(d => d.isNotSure),
    notSureList: breakdown.filter(d => d.isNotSure).map(d => d.name).join(', ')
  }
};
